import React, { Component } from 'react';
import { connect } from 'react-redux';

import { fetchMovie, setLoading } from '../../actions/searchActions';

import Spinner from '../layout/Spinner';

export class Movie extends Component {
  componentDidMount() {
    console.log(this.props.match.params.login);
    this.props.fetchMovie(this.props.match.params.login);
    this.props.setLoading();
  }
  render() {
    const { loading, x } = this.props;

    let movieInfo = (
      <div className="container">
        <div className="row">
          <div className="col-md-4 card card-body">
            <img src={x.avatar_url} className="thumbnail" alt="Poster" />
          </div>
          <div className="col-md-8">
            <h2 className="mb-4">{x.name}</h2>
            <ul className="list-group">
              <li className="list-group-item">
                <strong>Name: </strong> {x.name}
              </li>
              <li className="list-group-item">
                <strong>Location: </strong> {x.location}
              </li>
              <li className="list-group-item">
                <strong>email: </strong> {x.email === null ? "No email registered" : x.email}
              </li>
              <li className="list-group-item">
                <strong>Company: </strong> {x.company === null ? "No company registered" : x.company}
              </li>
              <li className="list-group-item">
                <strong>Login:</strong> {x.login}
              </li>
              <li className="list-group-item">
                <strong>Id:</strong> {x.id}
              </li>
              <li className="list-group-item">
              <strong>User </strong>
              <a
                href={x.url}
                target="_blank"
                
                rel="noopener noreferrer"
              >
               Link
              </a>
                
              </li>
              <li className="list-group-item">
              <strong>Github </strong>
              <a
                href={x.html_url}
                target="_blank"
                
                rel="noopener noreferrer"
              >
                 Link
              </a>
                
              </li>
              
              
            </ul>
          </div>
        </div>
        
      </div>
    );

    let content = loading ? <Spinner /> : movieInfo;
    return <div>{content}</div>;
  }
}

const mapStateToProps = state => ({
  loading: state.movies.loading,
  x: state.movies.x
});

export default connect(
  mapStateToProps,
  { fetchMovie, setLoading }
)(Movie);
