import React, { Component } from 'react';
import { connect } from 'react-redux';

import { fetchUser, setLoading } from '../../actions/searchActions';


export class User extends Component {
  componentDidMount() {
    console.log(this.props.match.params.login);
    this.props.fetchUser(this.props.match.params.login);
  }
  render() {
    const {  x } = this.props;
    console.log(x);
    let UserInfo = (
      <div className="container">
        <div className="row">
          <div className="col-md-4 card card-body">
            <img src={x.avatar_url} className="thumbnail" alt="Poster" />
          </div>
          <div className="col-md-8">
            <h2 className="mb-4">{x.name === null ? "User" : x.name}</h2>
            <ul className="list-group">
              <li className="list-group-item">
                <strong>Name: </strong> {x.name === null ? "No name registered" : x.name}
              </li>
              <li className="list-group-item">
                <strong>Location: </strong>  {x.location === null ? "No location registered" : x.location}
              </li>
              <li className="list-group-item">
                <strong>email: </strong> {x.email === null ? "No email registered" : x.email}
              </li>
              <li className="list-group-item">
                <strong>Company: </strong> {x.company === null ? "No company registered" : x.company}
              </li>
              <li className="list-group-item">
                <strong>Login:</strong>  {x.login === null ? "No login registered" : x.login}
              </li>
              <li className="list-group-item">
                <strong>Id:</strong>  {x.id === null ? "No id registered" : x.id}
              </li>
              <li className="list-group-item">
              <strong>User </strong>
              <a
                href={x.url}
                target="_blank"
                
                rel="noopener noreferrer"
              >
               Link
              </a>
                
              </li>
              <li className="list-group-item">
              <strong>Github </strong>
              <a
                href={x.html_url}
                target="_blank"
                
                rel="noopener noreferrer"
              >
                 Link
              </a>
                
              </li>
              
              
            </ul>
          </div>
        </div>
        
      </div>
    );

    let content = UserInfo;
    return <div>{content}</div>;
  }
}

const mapStateToProps = state => ({
  loading: state.users.loading,
  x: state.users.x
});

export default connect(
  mapStateToProps,
  { fetchUser, setLoading }
)(User);
