import {
  SEARCH_USER,
  FETCH_USERS,
  FETCH_USER,
  LOADING
} from '../actions/types';

const initialState = {
  text: '',
  users: [],
  loading: false,
  user: []
};

export default function(state = initialState, action) {
  switch (action.type) {
    case SEARCH_USER:
      return {
        ...state,
        text: action.payload,
        loading: false
      };
    case FETCH_USERS:
      return {
        ...state,
        users: action.payload,
        loading: false
      };
    case FETCH_USER:
      return {
        ...state,
        x: action.payload,
        loading: false
      };
    case LOADING:
      return {
        ...state,
        loading: true
      };
    default:
      return state;
  }
}
